type OrderStatus = "processing" | "shipped" | "completed" | "cancelled"

type OrderItem = {
  product: (typeof products)[0]
  quantity: number
  price: number
}

type Order = {
  id: number
  customer: (typeof customers)[0]
  date: string
  status: OrderStatus
  total: number
  items: OrderItem[]
  paymentMethod: "cash" | "card"
  deliveryAddress: string
}

export default function OrdersPage() {
  const [orders, setOrders] = useState<Order[]>(initialOrders)
  const [searchTerm, setSearchTerm] = useState("")
  const [isAddDialogOpen, setIsAddDialogOpen] = useState(false)
  const [isViewDialogOpen, setIsViewDialogOpen] = useState(false)
  const [selectedOrder, setSelectedOrder] = useState<Order | null>(null)
  const [activeTab, setActiveTab] = useState<OrderStatus | "all">("all")
  const [newOrder, setNewOrder] = useState({
    customerId: "",
    items: [{ productId: "", quantity: 1 }],
    paymentMethod: "cash",
    deliveryAddress: "",
  })

  const filteredOrders = orders.filter((order) => {
    const matchesSearch =
      order.customer.name.toLowerCase().includes(searchTerm.toLowerCase()) ||
      order.id.toString().includes(searchTerm) ||
      order.date.includes(searchTerm)

    const matchesTab = activeTab === "all" || order.status === activeTab

    return matchesSearch && matchesTab
  })

  const handleAddItem = () => {
    setNewOrder({
      ...newOrder,
      items: [...newOrder.items, { productId: "", quantity: 1 }],
    })
  }

  const handleRemoveItem = (index: number) => {
    const updatedItems = [...newOrder.items]
    updatedItems.splice(index, 1)
    setNewOrder({
      ...newOrder,
      items: updatedItems,
    })
  }

  const handleItemChange = (index: number, field: "productId" | "quantity", value: string | number) => {
    const updatedItems = [...newOrder.items]
    updatedItems[index] = {
      ...updatedItems[index],
      [field]: value,
    }
    setNewOrder({
      ...newOrder,
      items: updatedItems,
    })
  }

  const handleAddOrder = () => {
    // Oddiy validatsiya
    if (!newOrder.customerId || !newOrder.deliveryAddress || newOrder.items.some((item) => !item.productId)) {
      alert("Iltimos, barcha maydonlarni to'ldiring")
      return
    }

    const customer = customers.find((c) => c.id.toString() === newOrder.customerId)
    if (!customer) return

    const orderItems: OrderItem[] = newOrder.items.map((item) => {
      const product = products.find((p) => p.id.toString() === item.productId)
      if (!product) throw new Error("Product not found")
      return {
        product,
        quantity: Number(item.quantity),
        price: product.price * Number(item.quantity),
      }
    })

    const total = orderItems.reduce((sum, item) => sum + item.price, 0)

    const newOrderObj: Order = {
      id: Math.max(...orders.map((o) => o.id)) + 1,
      customer,
      date: new Date().toISOString().split("T")[0],
      status: "processing",
      total,
      items: orderItems,
      paymentMethod: newOrder.paymentMethod as "cash" | "card",
      deliveryAddress: newOrder.deliveryAddress,
    }

    setOrders([newOrderObj, ...orders])
    setIsAddDialogOpen(false)
    setNewOrder({
      customerId: "",
      items: [{ productId: "", quantity: 1 }],
      paymentMethod: "cash",
      deliveryAddress: "",
    })
  }

  const handleViewOrder = (order: Order) => {
    setSelectedOrder(order)
    setIsViewDialogOpen(true)
  }

  const getStatusBadge = (status: OrderStatus) => {
    switch (status) {
      case "processing":
        return (
          <Badge className="bg-blue-100 text-blue-800 hover:bg-blue-100 dark:bg-blue-900 dark:text-blue-300">
            Jarayonda
          </Badge>
        )
      case "shipped":
        return (
          <Badge className="bg-amber-100 text-amber-800 hover:bg-amber-100 dark:bg-amber-900 dark:text-amber-300">
            Yuborilgan
          </Badge>
        )
      case "completed":
        return (
          <Badge className="bg-green-100 text-green-800 hover:bg-green-100 dark:bg-green-900 dark:text-green-300">
            Yakunlangan
          </Badge>
        )
      case "cancelled":
        return (
          <Badge className="bg-red-100 text-red-800 hover:bg-red-100 dark:bg-red-900 dark:text-red-300">
            Bekor qilingan
          </Badge>
        )
    }
  }

  const getOrdersCountByStatus = (status: OrderStatus) => {
    return orders.filter((order) => order.status === status).length
  }

  return (
    <div className="p-6 space-y-6 animate-fade-in">
      <div className="flex items-center justify-between">
        <div>
          <h1 className="text-3xl font-bold">Buyurtmalar</h1>
          <p className="text-muted-foreground">Barcha buyurtmalarni boshqarish</p>
        </div>
        <Dialog open={isAddDialogOpen} onOpenChange={setIsAddDialogOpen}>
          <DialogTrigger asChild>
            <Button className="bg-primary hover:bg-primary/90">
              <Plus className="mr-2 h-4 w-4" />
              Yangi buyurtma
            </Button>
          </DialogTrigger>
          <DialogContent className="max-w-3xl">
            <DialogHeader>
              <DialogTitle>Yangi buyurtma qo&apos;shish</DialogTitle>
              <DialogDescription>Yangi buyurtma ma&apos;lumotlarini kiriting</DialogDescription>
            </DialogHeader>
            <div className="grid gap-4 py-4 max-h-[60vh] overflow-y-auto">
              <div className="grid gap-2">
                <Label htmlFor="customer">Mijoz</Label>
                <Select
                  value={newOrder.customerId}
                  onValueChange={(value) => setNewOrder({ ...newOrder, customerId: value })}
                >
                  <SelectTrigger id="customer">
                    <SelectValue placeholder="Mijozni tanlang" />
                  </SelectTrigger>
                  <SelectContent>
                    {customers.map((customer) => (
                      <SelectItem key={customer.id} value={customer.id.toString()}>
                        {customer.name} ({customer.phone})
                      </SelectItem>
                    ))}
                  </SelectContent>
                </Select>
              </div>

              <div className="space-y-2">
                <Label>Mahsulotlar</Label>
                {newOrder.items.map((item, index) => (
                  <div key={index} className="flex items-end gap-2">
                    <div className="flex-1">
                      <Label htmlFor={`product-${index}`} className="sr-only">
                        Mahsulot
                      </Label>
                      <Select
                        value={item.productId}
                        onValueChange={(value) => handleItemChange(index, "productId", value)}
                      >
                        <SelectTrigger id={`product-${index}`}>
                          <SelectValue placeholder="Mahsulotni tanlang" />
                        </SelectTrigger>
                        <SelectContent>
                          {products.map((product) => (
                            <SelectItem key={product.id} value={product.id.toString()}>
                              {product.name} ({product.price.toLocaleString()} so&apos;m)
                            </SelectItem>
                          ))}
                        </SelectContent>
                      </Select>
                    </div>
                    <div className="w-20">
                      <Label htmlFor={`quantity-${index}`} className="sr-only">
                        Miqdori
                      </Label>
                      <Input
                        id={`quantity-${index}`}
                        type="number"
                        min="1"
                        value={item.quantity}
                        onChange={(e) => handleItemChange(index, "quantity", Number.parseInt(e.target.value) || 1)}
                      />
                    </div>
                    <Button
                      variant="outline"
                      size="icon"
                      onClick={() => handleRemoveItem(index)}
                      disabled={newOrder.items.length === 1}
                    >
                      <span className="sr-only">O&apos;chirish</span>
                      <span></span>
                    </Button>
                  </div>
                ))}
                <Button variant="outline" size="sm" onClick={handleAddItem}>
                  <Plus className="mr-2 h-4 w-4" />
                  Mahsulot qo&apos;shish
                </Button>
              </div>

              <div className="grid gap-2">
                <Label htmlFor="paymentMethod">To&apos;lov usuli</Label>
                <Select
                  value={newOrder.paymentMethod}
                  onValueChange={(value) => setNewOrder({ ...newOrder, paymentMethod: value as "cash" | "card" })}
                >
                  <SelectTrigger id="paymentMethod">
                    <SelectValue placeholder="To'lov usulini tanlang" />
                  </SelectTrigger>
                  <SelectContent>
                    <SelectItem value="cash">Naqd pul</SelectItem>
                    <SelectItem value="card">Karta orqali</SelectItem>
                  </SelectContent>
                </Select>
              </div>

              <div className="grid gap-2">
                <Label htmlFor="deliveryAddress">Yetkazib berish manzili</Label>
                <Input
                  id="deliveryAddress"
                  value={newOrder.deliveryAddress}
                  onChange={(e) => setNewOrder({ ...newOrder, deliveryAddress: e.target.value })}
                  placeholder="Manzilni kiriting"
                />
              </div>
            </div>
            <DialogFooter>
              <Button variant="outline" onClick={() => setIsAddDialogOpen(false)}>
                Bekor qilish
              </Button>
              <Button onClick={handleAddOrder}>Saqlash</Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      </div>

      <div className="grid gap-4 md:grid-cols-4">
        <Card className="stats-card border-l-4 border-l-blue-500 shadow-md">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Jami buyurtmalar</CardTitle>
            <div className="h-8 w-8 rounded-full bg-blue-100 flex items-center justify-center">
              <Package className="h-4 w-4 text-blue-600" />
            </div>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{orders.length}</div>
            <div className="flex items-center mt-1">
              <span className="text-xs text-muted-foreground">So&apos;nggi 30 kun</span>
            </div>
          </CardContent>
        </Card>
        <Card className="stats-card border-l-4 border-l-blue-500 shadow-md">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Jarayondagi buyurtmalar</CardTitle>
            <div className="h-8 w-8 rounded-full bg-blue-100 flex items-center justify-center">
              <Package className="h-4 w-4 text-blue-600" />
            </div>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{getOrdersCountByStatus("processing")}</div>
            <div className="flex items-center mt-1">
              <span className="text-xs text-muted-foreground">Kutilmoqda</span>
            </div>
          </CardContent>
        </Card>
        <Card className="stats-card border-l-4 border-l-amber-500 shadow-md">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Yuborilgan buyurtmalar</CardTitle>
            <div className="h-8 w-8 rounded-full bg-amber-100 flex items-center justify-center">
              <Package className="h-4 w-4 text-amber-600" />
            </div>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{getOrdersCountByStatus("shipped")}</div>
            <div className="flex items-center mt-1">
              <span className="text-xs text-muted-foreground">Yo&apos;lda</span>
            </div>
          </CardContent>
        </Card>
        <Card className="stats-card border-l-4 border-l-green-500 shadow-md">
          <CardHeader className="flex flex-row items-center justify-between space-y-0 pb-2">
            <CardTitle className="text-sm font-medium">Yakunlangan buyurtmalar</CardTitle>
            <div className="h-8 w-8 rounded-full bg-green-100 flex items-center justify-center">
              <Package className="h-4 w-4 text-green-600" />
            </div>
          </CardHeader>
          <CardContent>
            <div className="text-2xl font-bold">{getOrdersCountByStatus("completed")}</div>
            <div className="flex items-center mt-1">
              <span className="text-xs text-muted-foreground">Mijozlarga yetkazilgan</span>
            </div>
          </CardContent>
        </Card>
      </div>

      <Card className="shadow-md">
        <CardHeader className="border-b bg-muted/30 py-4">
          <div className="flex flex-col sm:flex-row sm:items-center justify-between gap-4">
            <Tabs
              defaultValue="all"
              value={activeTab}
              onValueChange={(value) => setActiveTab(value as OrderStatus | "all")}
              className="w-full max-w-md"
            >
              <TabsList className="grid grid-cols-5">
                <TabsTrigger value="all">Hammasi</TabsTrigger>
                <TabsTrigger value="processing">Jarayonda</TabsTrigger>
                <TabsTrigger value="shipped">Yuborilgan</TabsTrigger>
                <TabsTrigger value="completed">Yakunlangan</TabsTrigger>
                <TabsTrigger value="cancelled">Bekor qilingan</TabsTrigger>
              </TabsList>
            </Tabs>
            <div className="flex gap-2 items-center">
              <div className="relative max-w-sm">
                <Search className="absolute left-2.5 top-2.5 h-4 w-4 text-muted-foreground" />
                <Input
                  type="search"
                  placeholder="Buyurtmalarni qidirish..."
                  className="pl-8 w-[200px]"
                  value={searchTerm}
                  onChange={(e) => setSearchTerm(e.target.value)}
                />
              </div>
              <Button variant="outline" size="sm" className="h-9">
                <Filter className="mr-2 h-4 w-4" />
                Filtrlash
              </Button>
            </div>
          </div>
        </CardHeader>
        <CardContent className="p-0">
          <div className="rounded-md overflow-hidden">
            <Table>
              <TableHeader>
                <TableRow className="bg-muted/50 hover:bg-muted/50">
                  <TableHead>Buyurtma ID</TableHead>
                  <TableHead>Mijoz</TableHead>
                  <TableHead>Sana</TableHead>
                  <TableHead>Holati</TableHead>
                  <TableHead>Jami summa</TableHead>
                  <TableHead className="text-right">Amallar</TableHead>
                </TableRow>
              </TableHeader>
              <TableBody>
                {filteredOrders.length > 0 ? (
                  filteredOrders.map((order) => (
                    <TableRow key={order.id} className="hover:bg-muted/30">
                      <TableCell className="font-medium">#{order.id}</TableCell>
                      <TableCell>{order.customer.name}</TableCell>
                      <TableCell>{new Date(order.date).toLocaleDateString("uz-UZ")}</TableCell>
                      <TableCell>{getStatusBadge(order.status)}</TableCell>
                      <TableCell>{order.total.toLocaleString()} so&apos;m</TableCell>
                      <TableCell className="text-right">
                        <Button
                          variant="ghost"
                          size="icon"
                          onClick={() => handleViewOrder(order)}
                          className="text-blue-600 hover:text-blue-700 hover:bg-blue-50"
                        >
                          <Eye className="h-4 w-4" />
                        </Button>
                      </TableCell>
                    </TableRow>
                  ))
                ) : (
                  <TableRow>
                    <TableCell colSpan={6} className="h-24 text-center">
                      Buyurtmalar topilmadi
                    </TableCell>
                  </TableRow>
                )}
              </TableBody>
            </Table>
          </div>
        </CardContent>
      </Card>

      {selectedOrder && (
        <Dialog open={isViewDialogOpen} onOpenChange={setIsViewDialogOpen}>
          <DialogContent className="max-w-3xl">
            <DialogHeader>
              <DialogTitle>Buyurtma #{selectedOrder.id}</DialogTitle>
              <DialogDescription>
                {new Date(selectedOrder.date).toLocaleDateString("uz-UZ")} - {getStatusBadge(selectedOrder.status)}
              </DialogDescription>
            </DialogHeader>
            <div className="grid gap-4 py-4 max-h-[60vh] overflow-y-auto">
              <div className="grid md:grid-cols-2 gap-4">
                <div>
                  <h3 className="text-sm font-medium text-muted-foreground mb-2">Mijoz ma&apos;lumotlari</h3>
                  <div className="space-y-1">
                    <p className="font-medium">{selectedOrder.customer.name}</p>
                    <p className="text-sm">{selectedOrder.customer.phone}</p>
                    <p className="text-sm">{selectedOrder.customer.email}</p>
                  </div>
                </div>
                <div>
                  <h3 className="text-sm font-medium text-muted-foreground mb-2">Yetkazib berish ma&apos;lumotlari</h3>
                  <div className="space-y-1">
                    <p className="text-sm">{selectedOrder.deliveryAddress}</p>
                    <p className="text-sm">
                      To&apos;lov usuli: {selectedOrder.paymentMethod === "cash" ? "Naqd pul" : "Karta orqali"}
                    </p>
                  </div>
                </div>
              </div>

              <div>
                <h3 className="text-sm font-medium text-muted-foreground mb-2">Buyurtma mahsulotlari</h3>
                <div className="rounded-md border">
                  <Table>
                    <TableHeader>
                      <TableRow className="bg-muted/50 hover:bg-muted/50">
                        <TableHead>Mahsulot</TableHead>
                        <TableHead>Miqdori</TableHead>
                        <TableHead>Narxi</TableHead>
                        <TableHead className="text-right">Jami</TableHead>
                      </TableRow>
                    </TableHeader>
                    <TableBody>
                      {selectedOrder.items.map((item, index) => (
                        <TableRow key={index}>
                          <TableCell>
                            <div className="flex items-center gap-2">
                              <img
                                src={item.product.image || "/placeholder.svg"}
                                alt={item.product.name}
                                className="h-8 w-8 rounded-md object-cover"
                              />
                              <span>{item.product.name}</span>
                            </div>
                          </TableCell>
                          <TableCell>{item.quantity}</TableCell>
                          <TableCell>{(item.price / item.quantity).toLocaleString()} so&apos;m</TableCell>
                          <TableCell className="text-right">{item.price.toLocaleString()} so&apos;m</TableCell>
                        </TableRow>
                      ))}
                      <TableRow>
                        <TableCell colSpan={3} className="text-right font-medium">
                          Jami summa:
                        </TableCell>
                        <TableCell className="text-right font-bold">
                          {selectedOrder.total.toLocaleString()} so&apos;m
                        </TableCell>
                      </TableRow>
                    </TableBody>
                  </Table>
                </div>
              </div>
            </div>
            <DialogFooter>
              <Button variant="outline" onClick={() => setIsViewDialogOpen(false)}>
                Yopish
              </Button>
            </DialogFooter>
          </DialogContent>
        </Dialog>
      )}
    </div>
  )
}
